func getAppToken(loginToken string) string {
	url := "https://account-cn.huami.com/v1/client/app_tokens?app_name=com.xiaomi.hm.health&dn=api-user.huami.com%2Capi-mifit.huami.com%2Capp-analytics.huami.com&login_token=" + loginToken
	req2, _ := http.NewRequest(http.MethodGet, url, nil)
	req2.Header.Set("User-Agent", "MiFit/5.3.0 (iPhone; iOS 14.7.1; Scale/3.00)")
	resp2, _ := http.DefaultClient.Do(req2)
	if resp2.StatusCode != 200 {
		fmt.Printf("修改失败，status: %d\n", resp2.StatusCode)
		return ""
	}
	res, _ := io.ReadAll(resp2.Body)
	info := appInfo{}
	json.Unmarshal(res, &info)
	return info.TokenInfo.AppToken
}

type appInfo struct {
	TokenInfo struct {
		AppToken string `json:"app_token"`
	} `json:"token_info"`
}

func getAccessToken(location string) string {
	// 定义正则表达式模式，匹配整个access=...部分，并捕获其中的内容
	codePattern := regexp.MustCompile(`access=([^&$]*)`)

	// 在给定的location中查找匹配的部分
	match := codePattern.FindStringSubmatch(location)

	// 如果未找到匹配项，则返回空字符串
	if len(match) < 2 {
		return ""
	}
	// 返回捕获的内容
	return match[1]
}
